UINT32 serdes_port_id[6] = { 0, 4, 8, 12, 16, 20};

for (UINT32 i = 0; i < 6 && MSCC_SUCCESS == rc; i++)
{
    // Assign the LOS pin to the base lane for each 100G port
    rc = g5_api_serdes_port_los_cfg(device_id,
                                    serdes_port_id[i],
                                    TRUE,   // dlos_enable
                                    FALSE,  // los_inv
                                    i,      // los_pin
                                    FALSE); // los_enable
    MSCC_ASSERT(MSCC_SUCCESS == rc, 0);
}
